from django.db import models
from django.contrib.auth.models import AbstractBaseUser


class Vendor(AbstractBaseUser):
    vendor_code = models.CharField(max_length=20, unique=True)
    password = models.CharField(max_length=200)
    name =  models.CharField(max_length=50)
    contact_details = models.CharField(max_length=15)
    address = models.TextField()
    on_time_delivery_rate = models.FloatField(null=True)
    quality_rating_avg = models.FloatField(null=True)
    average_response_time = models.FloatField(null=True)
    fulfillment_rate = models.FloatField(null=True)
    

    USERNAME_FIELD = 'vendor_code'
    REQUIRED_FIELDS = ['name']


    '''
    There is no significant use of unique vendor code as vendor_id generated by
     model itself is always unique as it is auto increment '''
    